import React, { useState } from 'react';

function FilmAddComponent(props) {
  const [film, setFilm] = useState({
    title: "",
    description: "",
    releaseYear: "",
    languageId: ""

  });

  const handleChange = (e) => {
    const { attributeName, value } = e.target;
    setFilm(prevFilm => ({ ...prevFilm, [attributeName]: value }));
  };

  const addFilm = async () => {
    try {
      const response = await fetch('/films/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(film),
      });

      if (response.ok) {
        alert("Film added successfully");
      } else {
        alert("Failed to add film");
      }
    } catch (error) {
      console.error("Error adding film:", error);
      alert("Error adding film");
    }
  };

  return (
    <div>
      <input 
        type="text" 
        name="title"
        value={film.title} 
        onChange={handleChange} 
        placeholder="Title"
      />
      <input 
        type="text" 
        name="description"
        value={film.description} 
        onChange={handleChange} 
        placeholder="Description"
      />
      {/* Add other input fields with corresponding name attributes... */}
      <button onClick={addFilm}>Add Film</button>
    </div>
  );
}

export default FilmAddComponent;
